import React from 'react';

function Awards() {
    return ( 
        <>
        <div className='container mt-5'>
            <div className='row'>
                <div className='col-6 fw-light'>
                    <h2 className='fw-normal mb-5 fs-3'>Trust with confidence</h2>
                    <h3 className='fw-normal fs-4'>Customer-first always</h3>
                    <p>That's why 1.6+ crore customers trust Zerodha with ~ ₹6 lakh crores of equity investments, making us India’s largest broker; contributing to 15% of daily retail exchange volumes in India.</p>
                    <h3 className='fw-normal fs-4 mt-4'>No spam or gimmicks</h3>
                    <p>No gimmicks, spam, "gamification", or annoying push notifications. High quality apps that you use at your pace, the way you like. Our philosophies.</p>
                    <h3 className='fw-normal fs-4 mt-4'>The Zerodha universe</h3>
                    <p>Not just an app, but a whole ecosystem. Our investments in 30+ fintech startups offer you tailored services specific to your needs.</p>
                    <h3 className='fw-normal fs-4 mt-4'>Do better with money</h3>
                    <p>With initiatives like Nudge and Kill Switch, we don't just facilitate transactions, but actively help you do better with your money.</p>
                </div>
              <div className='col-6'>
    <img src='media/images/ecosystem.png' className='img-fluid' style={{width:'600px', height:'auto'}} />
    
    {/* Wrap the links in a flex container to put them on the same line and center them */}
    <div className='d-flex justify-content-center mt-3'>
        {/* Add margin to the right of the first link (me-3) for spacing */}
        <a className='me-3'>Explore our products</a>
        
        {/* The second link */}
        <a>Try Kite demo</a>
    </div>
</div>
            </div>
        </div>
        </>
     );
}

export default Awards;
